package basicLearingJava;

public class Program_3_Calculation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 8;
		System.out.println(a >> 1); // 8 / 2
		System.out.println(a >> 2); // 8 / 4
		System.out.println(a >> 3); // 8 / 8
		System.out.println(a << 1); // 8 * 2
		System.out.println(a << 2); // 8 * 4
		System.out.println(a << 3); // 8 * 8
	}

}
